import { useEffect, useState } from "react";
import "./Header.css";



function Header(){

  const [scrollVal, setScrollVal] = useState<any>(0);
  
  
  useEffect(() =>{
    const handleScroll = () => {
      setScrollVal(window.scrollY);
    };

    window.addEventListener("scroll", handleScroll);

    return () => {
      window.removeEventListener("scroll", handleScroll);
    };
  }, []);
  
  
  return (
    <header>
      <div className="bg-images">
        <div className="back-layer"></div>
        <div className="mid-layer" 
        style={{transform: `translateY(${0 + scrollVal / 3}px)`}}
        ></div>
        <div className="front-layer"></div>
      </div>
      <div className="content">
        <div className="header-cont">
          <div className="header-context" 
          style={{transform: `translateY(${scrollVal / 4.4}px)`}}>
            <div>
              <h1>KRAI*</h1>
            </div>
            <div>
              <p>
                Join us for a week of hiking, team-bonding
                and digital detoxing. This month, one-time only in
                Kamchatka.
              </p>
              <a href="/">Adventure  â€£</a>
            </div>
          </div>
        </div>
      </div>
    </header>
  )
}

export default Header;
