import { useEffect, useRef, useState } from "react";
import "./Carusel.css"


function Carusel() {

  const [carusellIndex, setCaruselIndex] = useState<number>(0);
  const getIndex = useRef<any>(0)

  useEffect(() =>{
    const chileLenght = Array.from(getIndex.current.children).length;


  }, [])

  const slideLeft = () =>{
    setCaruselIndex (carusellIndex  - 100)
    console.log(carusellIndex); 
  }
  const slideRight = () =>{
    setCaruselIndex (carusellIndex  - 100)
    console.log(carusellIndex); 
  }
  
  
  
  
  return(
    <div className="carusel-cont">
      <button onClick={() => slideLeft()}>Left</button>
      <div className="main-carusel" ref={getIndex}>
        <div className="cards-cont" 
        style={{transform : `translateX( ${carusellIndex}%)`}}
        >
          <div className="card"></div>
          <div className="card"></div>
          <div className="card"></div>
        </div>
        <div className="cards-cont"
        style={{transform : `translateX( ${carusellIndex}%)`}}
        >
          <div className="card"></div>
          <div className="card"></div>
          <div className="card"></div>
        </div>
      </div>
      <button onClick={() => slideRight()}>Right</button>
    </div>
  );
}


export default Carusel
